import { createStore } from 'redux';

const initialState = {
    success: true,
    isFetching: false
};

const reducer = (state = initialState, action) => {
    switch (action.type) {
        case 'REQUEST':
            return {
                success: false,
                isFetching: true,
                method: action.method
            };
        case 'RECEIVE':
            return {
                success: true,
                isFetching: false,
                content: action.content,
                method: action.method
            };
        default:
            return state;
    }
}

const store = createStore(() => { return { response: reducer } });

export default store
