import axios from 'axios'
import { begin, success, error } from './actions'

const fetchContent = (options) => {
    const requestOptions = Object.assign({}, {
        baseURL: 'https://post-service-fe827.firebaseapp.com/api/v1',
        method: 'get',
        headers: {
            'Accept': 'application/json',
            'Content-Type': 'application/x-www-form-urlencoded'  //'application/json' was sending options as method instead of post
        }
    }, options)

    // return dispatch => {
    //     dispatch(begin(requestOptions.method));
        return axios.request(requestOptions)
            .then(res => {
                return res.json()
            })
            .then(json => {
                return json//dispatch(success(requestOptions.method, json));
            })
            .catch(err => {
                return err//dispatch(error(requestOptions.method, err));
            });
    //};
}

const get = () => {
    return fetchContent({
        method: 'get',
        url: '/feeds'
    })
}

const post = (data) => {
    return fetchContent({
        method: 'post',
        url: '/feeds',
        data
    })
}

const upvote = (id, data) => {
    return fetchContent({
        method: 'put',
        url: `/feeds/upvote/${id}`,
        data
    })
}

export default {
    get,
    post,
    upvote
}
