const axios = require('axios')
import { REQUEST, RESPONSE } from './actions'

const request = (options) => {
    const requestOptions = Object.assign({}, {
        baseURL: 'https://post-service-fe827.firebaseapp.com/api/v1',
        method: 'get',
        headers: {
            'Accept': 'application/json',
            'Content-Type': 'application/json'  //'application/json' was sending options as method instead of post
        }
    }, options)

    return axios.request(requestOptions)
        .then(response => {
            return (dispatch) => {
                return dispatch({ type: RECEIVE, method: requestOptions.method, contenr: response.data });
            }
        })
        .catch(error => {
            console.log(error)
        })
}

const get = () => {
    return request({
        method: 'get',
        url: '/feeds'
    })
}

const post = (data) => {
    return request({
        method: 'post',
        url: '/feeds',
        data
    })
}

const upvote = (id, data) => {
    return request({
        method: 'put',
        url: `/feeds/upvote/${id}`,
        data
    })
}

module.exports = {
    get,
    post,
    upvote
}
