const axios = require('axios')
import { begin, success } from './actions'

const fetchContent = (options) => {
    const requestOptions = Object.assign({}, {
        baseURL: 'https://post-service-fe827.firebaseapp.com/api/v1',
        method: 'get',
        headers: {
            'Accept': 'application/json',
            'Content-Type': 'application/json'  //'application/json' was sending options as method instead of post
        }
    }, options)

    return dispatch => {
        dispatch(begin(requestOptions.method));
        return axios.request(requestOptions)
            .then(res => res.json())
            .then(json => {
                return dispatch(success(requestOptions.method, json));
            })
            .catch(error => console.log(error));
    };
}

const get = () => {
    return fetchContent({
        method: 'get',
        url: '/feeds'
    })
}

const post = (data) => {
    return fetchContent({
        method: 'post',
        url: '/feeds',
        data
    })
}

const upvote = (id, data) => {
    return fetchContent({
        method: 'put',
        url: `/feeds/upvote/${id}`,
        data
    })
}

module.exports = {
    get,
    post,
    upvote
}
