const axios = require('axios')

const request = (options) => {
    const requestOptions = Object.assign({}, {
        baseURL: 'https://post-service-fe827.firebaseapp.com/api/v1',
        method: 'get',
        headers: {
            'Accept': 'application/json',
            'Content-Type': 'application/json'  //'application/json' was sending options as method instead of post
        }
    }, options)

    return axios.request(requestOptions)
        .then(response => {
            return response.data
        })
        .catch(error => {
            console.log(error)
        })
}

const get = () => {
    return request({
        method: 'get',
        url: '/getPosts'
    })
}

const post = (data) => {
    return request({
        method: 'post',
        url: '/addPosts',
        data
    })
}

const upvote = (data) => {
    return request({
        method: 'put',
        url: `/upvote/${data}`
    })
}

module.exports = {
    get,
    post,
    upvote
}
