import React, { Component } from 'react';
import 'bootstrap/dist/css/bootstrap.min.css';
import { Button } from 'react-bootstrap'
import { get, post, upvote } from './api-connect'
import dateformat from 'dateformat'

class App extends Component {

  constructor(props) {
    super(props)

    this.state = {
      data: []
    }

    this.handleAddNewPost = this.handleAddNewPost.bind(this)
    // this.addNewPost = this.addNewPost.bind(this)
    //this.handleChange = this.handleChange.bind(this)
  }

  componentDidMount() {
    get()
      .then(res => {
        this.setState({ data: res })
      })
  }

  handleAddNewPost() {
    this.setState({ showInput: true })
  }

  addNewPost() {
    post({ text: this.state.text })
      .then(() => {
        return get()
          .then(res => {
            this.setState({ data: res, showInput: false })
          })
      })
  }

  upvotePost(id) {
    upvote(id)
      .then(() => {
        return get()
          .then(res => {
            this.setState({ data: res })
          })
      })
  }

  handleChange(e) {
    this.setState({ text: e.target.value })
  }

  render() {
    return (
      <div>
        <div>POSTS:</div>
        <div>
          {this.state.data.map((d, i) => {
            return (
              <div key={i}>{d.text}
                <span style={{ padding: '10px' }}>
                  <span>{dateformat(d.createdOn, 'mmm dd, yyyy, hh:MM tt')}</span>
                </span>
                <Button bsStyle='link' onClick={this.upvotePost.bind(this, d.id)}>
                  {`${d.upvotes} Upvotes`}
                </Button>
              </div>
            )
          })}
        </div>
        {!this.state.showInput ?
          (<Button bsStyle='link' onClick={this.handleAddNewPost}>Add</Button>) :
          [<input key={0} onChange={this.handleChange} type='text'></input>,
          <Button bsStyle='link' key={1} onClick={this.addNewPost}>Add Post</Button>]
        }
      </div>
    );
  }
}

export default App;
