import { createStore } from 'redux';

const initialState = {
    success: true,
    isFetching: false
};

const reducer = (state = initialState, action) => {
    switch (action.type) {
        case 'REQUEST':
            return {
                success: false,
                isFetching: true
            };
        case 'RECEIVE':
            return {
                success: true,
                isFetching: false,
                content: action.content
            };
        default:
            return state;
    }
}

const store = createStore({ response: reducer });

export default store
